export default (posts = [], action) => {
    switch (action.type) {
        case "FETCH_ALL":
            return action.payload;
        case "CREATE":
            return [...posts, action.payload]
        case "update":
            return posts.map((post) => post.id)

        default:
            return posts;
    }
}
