import axios from 'axios';

// const url = 'http://localhost:5000/posts';
const API = axios.create({baseURL: 'http://localhost:5000'});

export const fetchPosts = () => A.get('/posts');
export const createPost = (newPost) => A.post('/posts', newPost);
export const likePost = (id) => A.patch(`/posts/${id}/likePost`);
export const updatePost = (id, updatedPost) => A.patch(`/posts/${id}`, updatedPost);
export const deletePost = (id) => A.delete(`/posts/${id}`);

export const signIn = (formData) => A.patch('/user/signin',formData);
export const signUp = (formData) => A.patch('/user/signup',formData);
